# record的key是sqe, value裡面的元素的key是type, value是text
for sqe, record in records.items():
    if sqe not in available_sqe_list:
        print(f"Skip sqe {sqe}")
        continue
    for type_ABC, part_text in record.items():
        if type_ABC not in available_types:
            print(f"Skip type {type_ABC}")
            continue
        payload = {
            "file_id": str(uuid.uuid4()),
            "sqe": sqe,
            "type": type_ABC,
            "text": part_text
        }
        print(payload)
        headers = {
            'Content-Type': 'application/json'
        }
        response = requests.request("POST", url, headers=headers, data=json.dumps(payload))
        print(response.text)

